package ch3;

public class OperatorEx19 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 8;
		// %연산은 주로 짝,홀수 또는 배수검사 등에 사용
		System.out.printf("%d을 %d로 나누면, %n", x, y);
		System.out.printf("몫은 %d이고, 나머지는 %d입니다.%n", x / y, x % y);
	}

}
